import { useState } from "react";
import { useQuery, useMutation } from "@tanstack/react-query";
import {
  Dialog,
  DialogContent,
  DialogDescription,
  DialogHeader,
  DialogTitle,
} from "@/components/ui/dialog";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import {
  Table,
  TableBody,
  TableCell,
  TableHead,
  TableHeader,
  TableRow,
} from "@/components/ui/table";
import { Plus } from "lucide-react";
import { studentApi, paymentApi } from "@/lib/api";
import { queryClient } from "@/lib/queryClient";
import { useToast } from "@/hooks/use-toast";

interface PaymentHistoryDialogProps {
  open: boolean;
  onOpenChange: (open: boolean) => void;
  studentId: string;
  batchFee: number;
  feePeriod: string;
}

export default function PaymentHistoryDialog({
  open,
  onOpenChange,
  studentId,
  batchFee,
  feePeriod,
}: PaymentHistoryDialogProps) {
  const { toast } = useToast();
  const [amount, setAmount] = useState("");
  const [showAddPayment, setShowAddPayment] = useState(false);

  const { data: studentData, isLoading } = useQuery({
    queryKey: ["/api/students", studentId],
    queryFn: () => studentApi.get(studentId),
    enabled: open && !!studentId,
  });

  const addPaymentMutation = useMutation({
    mutationFn: paymentApi.create,
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/students", studentId] });
      queryClient.invalidateQueries({ queryKey: ["/api/batches"] });
      queryClient.invalidateQueries({ queryKey: ["/api/stats/teacher"] });
      setAmount("");
      setShowAddPayment(false);
      toast({
        title: "Payment recorded!",
        description: `₹${amount} payment has been recorded`,
      });
    },
    onError: (error: Error) => {
      toast({
        title: "Error recording payment",
        description: error.message,
        variant: "destructive",
      });
    },
  });

  const student = studentData?.student;
  const payments = studentData?.payments || [];
  const totalPaid = studentData?.totalPaid || 0;

  // Calculate expected total fee based on period and join date
  let expectedTotalFee = batchFee;
  if (student && feePeriod === "month") {
    const joinMonths = Math.ceil(
      (new Date().getTime() - new Date(student.joinDate).getTime()) / (1000 * 60 * 60 * 24 * 30)
    );
    expectedTotalFee = batchFee * joinMonths;
  }

  const remaining = expectedTotalFee - totalPaid;

  const handleAddPayment = (e: React.FormEvent) => {
    e.preventDefault();
    const paymentAmount = Number(amount);

    if (paymentAmount <= 0) {
      toast({
        title: "Invalid amount",
        description: "Payment amount must be greater than 0",
        variant: "destructive",
      });
      return;
    }

    if (paymentAmount > remaining) {
      toast({
        title: "Amount exceeds remaining balance",
        description: `Maximum payment allowed is ₹${remaining.toLocaleString()}`,
        variant: "destructive",
      });
      return;
    }

    addPaymentMutation.mutate({
      studentId,
      amount: paymentAmount,
    });
  };

  return (
    <Dialog open={open} onOpenChange={onOpenChange}>
      <DialogContent className="max-w-2xl">
        <DialogHeader>
          <DialogTitle>Payment History - {student?.fullName || "Loading..."}</DialogTitle>
          <DialogDescription>
            View and manage payment records
          </DialogDescription>
        </DialogHeader>
        {isLoading ? (
          <div className="py-8 text-center text-muted-foreground">Loading payment history...</div>
        ) : (
          <div className="space-y-4 py-4">
            <div className="grid grid-cols-3 gap-4 p-4 bg-muted/50 rounded-md">
              <div>
                <p className="text-sm text-muted-foreground">Total Fee</p>
                <p className="text-2xl font-bold">₹{expectedTotalFee.toLocaleString()}</p>
              </div>
              <div>
                <p className="text-sm text-muted-foreground">Total Paid</p>
                <p className="text-2xl font-bold text-chart-2">₹{totalPaid.toLocaleString()}</p>
              </div>
              <div>
                <p className="text-sm text-muted-foreground">Remaining</p>
                <p className="text-2xl font-bold text-chart-3">₹{remaining.toLocaleString()}</p>
              </div>
            </div>

            <div className="border rounded-md">
              <Table>
                <TableHeader>
                  <TableRow>
                    <TableHead>Date</TableHead>
                    <TableHead className="text-right">Amount Paid</TableHead>
                    <TableHead className="text-right">Remaining</TableHead>
                  </TableRow>
                </TableHeader>
                <TableBody>
                  {payments.length === 0 ? (
                    <TableRow>
                      <TableCell colSpan={3} className="text-center py-8 text-muted-foreground">
                        No payments recorded yet
                      </TableCell>
                    </TableRow>
                  ) : (
                    payments.map((payment, index) => {
                      const paidSoFar = payments
                        .slice(0, index + 1)
                        .reduce((sum, p) => sum + p.amount, 0);
                      const remainingAtTime = expectedTotalFee - paidSoFar;

                      return (
                        <TableRow key={payment.id}>
                          <TableCell>{new Date(payment.paidAt).toLocaleDateString()}</TableCell>
                          <TableCell className="text-right font-mono">
                            ₹{payment.amount.toLocaleString()}
                          </TableCell>
                          <TableCell className="text-right font-mono">
                            ₹{remainingAtTime.toLocaleString()}
                          </TableCell>
                        </TableRow>
                      );
                    })
                  )}
                </TableBody>
              </Table>
            </div>

            {showAddPayment ? (
              <form onSubmit={handleAddPayment} className="space-y-4 p-4 border rounded-md">
                <div className="space-y-2">
                  <Label htmlFor="amount">Payment Amount (₹)</Label>
                  <Input
                    id="amount"
                    type="number"
                    placeholder="5000"
                    value={amount}
                    onChange={(e) => setAmount(e.target.value)}
                    min="1"
                    max={remaining}
                    required
                    data-testid="input-payment-amount"
                  />
                  <p className="text-sm text-muted-foreground">
                    Maximum amount: ₹{remaining.toLocaleString()}
                  </p>
                </div>
                <div className="flex gap-2">
                  <Button 
                    type="submit" 
                    disabled={addPaymentMutation.isPending}
                    data-testid="button-submit-payment"
                  >
                    {addPaymentMutation.isPending ? "Adding..." : "Add Payment"}
                  </Button>
                  <Button
                    type="button"
                    variant="outline"
                    onClick={() => {
                      setShowAddPayment(false);
                      setAmount("");
                    }}
                  >
                    Cancel
                  </Button>
                </div>
              </form>
            ) : (
              <Button
                onClick={() => setShowAddPayment(true)}
                className="w-full"
                disabled={remaining <= 0}
                data-testid="button-add-payment"
              >
                <Plus className="w-4 h-4 mr-2" />
                {remaining <= 0 ? "Fully Paid" : "Add Payment"}
              </Button>
            )}
          </div>
        )}
      </DialogContent>
    </Dialog>
  );
}

client/src/App.tsx.content:
import { Switch, Route, useLocation } from "wouter";
import { queryClient } from "./lib/queryClient";
import { QueryClientProvider, useQuery } from "@tanstack/react-query";
import { Toaster } from "@/components/ui/toaster";
import { TooltipProvider } from "@/components/ui/tooltip";
import { authApi } from "@/lib/api";
import LoginPage from "@/pages/LoginPage";
import TeacherDashboard from "@/pages/TeacherDashboard";
import BatchDetailsPage from "@/pages/BatchDetailsPage";
import SuperAdminDashboard from "@/pages/SuperAdminDashboard";
import StudentRegistrationPage from "@/pages/StudentRegistrationPage";
import NotFound from "@/pages/not-found";

function Router() {
  const [, setLocation] = useLocation();

  const { data: userData, isLoading } = useQuery<{ user: any }>({
    queryKey: ["/api/auth/me"],
    retry: false,
  });

  const handleLogin = async (username: string, password: string) => {
    try {
      await authApi.login(username, password);
      await queryClient.invalidateQueries({ queryKey: ["/api/auth/me"] });
      setLocation("/");
    } catch (error) {
      throw error;
    }
  };

  if (isLoading) {
    return (
      <div className="min-h-screen flex items-center justify-center bg-background">
        <div className="text-muted-foreground">Loading...</div>
      </div>
    );
  }

  const user = userData?.user;

  return (
    <Switch>
      <Route path="/register/:token" component={StudentRegistrationPage} />
      <Route path="/batch/:id">
        {(params) => {
          if (!user) return <LoginPage onLogin={handleLogin} />;
          return <BatchDetailsPage batchId={params.id} />;
        }}
      </Route>
      <Route path="/">
        {() => {
          if (!user) {
            return <LoginPage onLogin={handleLogin} />;
          }
          if (user.role === 'superadmin') {
            return <SuperAdminDashboard />;
          }
          return <TeacherDashboard />;
        }}
      </Route>
      <Route component={NotFound} />
    </Switch>
  );
}

function App() {
  return (
    <QueryClientProvider client={queryClient}>
      <TooltipProvider>
        <Toaster />
        <Router />
      </TooltipProvider>
    </QueryClientProvider>
  );
}

export default App;

